function chainedErrors() {
  return Promise.resolve(1)
    .then(v => { throw new Error(`step1: ${v}`); })
    .then(v => v + 1) // skipped
    .catch(err => `caught: ${err.message}`)
    .then(v => `final: ${v}`);
}

