// --- Finally for cleanup ---

function withCleanup(resource) {
  let handle;
  try {
    handle = openResource(resource);
    return processResource(handle);
  } catch (err) {
    logError(err);
    throw err;
  } finally {
    if (handle) closeResource(handle);
  }
}

