    let current = this.start;
    const end = this.end;
    const delay = this.delayMs;
    return {
      async next() {
        if (current <= end) {
          await new Promise(r => setTimeout(r, delay));
          return { value: current++, done: false };
        }
        return { value: undefined, done: true };
      },
