function* zipIter(a, b) {
  const iterA = a[Symbol.iterator]();
  const iterB = b[Symbol.iterator]();
  while (true) {
    const stepA = iterA.next();
    const stepB = iterB.next();
    if (stepA.done || stepB.done) return;
    yield [stepA.value, stepB.value];
  }
}

